import { useState } from 'react';

function App() {
	// 아래는 비구조화 할당 useState(초기값세팅);
	const { time, setTime } = useState(3);

	function UpTime() {
		setTime(time + 1);
	}

	return (
		<div>
			<span>현재 시각 : {time}시</span>
			<button onClick={UpTime}>Update</button>
		</div>
	);
}

export default App;
